INSERT INTO `products` (`product_id`, `code_product`, `description`, `discount`, `entered_date`, `image`, `image2`, `image3`, `image4`, `image5`, `name`, `price`, `quantity`, `sold`, `status`, `category_id`, `supplier_id`) VALUES
	(1, 'MSPTH00001', 'Chuột gaming không dây Logitech G304 Lightspeed là sản phẩm đang làm mưa làm gió trên thị trường với nhiều tính năng đặc biệt nổi bật. Với nhiều công phá mới trong thiết kế cùng mức giá thành tương đối rẻ.', 21, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732817569/techhalo/product-image/v7y2tqw1uwxymm0bbqfc.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817574/techhalo/product-image/u3i2ncj1upg7eqif7tdv.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817580/techhalo/product-image/drqjfpsrm6lg6spzaqes.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817597/techhalo/product-image/voqw8phdbgiow3xes2ko.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817585/techhalo/product-image/reaz1ypvlmytzlkvxnah.webp', 'Chuột Gaming không dây Logitech G304 Lightspeed', 949000, 999, 0, b'1', 5, 13),
	(2, 'MSPTH00002', 'Sở hữu công nghệ Lightsync, mang thiết kế 6 nút cổ điển cùng DPI lên đến 8.000, chuột Gaming Logitech G102 LightSync sẽ giúp bạn chinh phục mọi tựa game mà bạn yêu thích.', 40, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732817902/techhalo/product-image/lxsg6didrzvzsbp6zyx7.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817908/techhalo/product-image/glccj76yrt1sl5q2cuse.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817930/techhalo/product-image/caokbujanobao8ldasm3.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817937/techhalo/product-image/bg6vaolnpai7tjknr3nf.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732817942/techhalo/product-image/judxqajgvpavx4tpceg6.webp', 'Chuột có dây Gaming Logitech G102 LightSync Gen 2', 599000, 999, 0, b'1', 5, 13),
	(3, 'MSPTH00003', 'IPad, Macbook, iMac của bạn đang cần đến một con chuột nhanh nhạy tối ưu điểm chạm hơn? Tất cả những gì bạn cần làm là đổi qua sử dụng chuột Apple Magic Mouse 2021 MK2E3. ', 17, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732818267/techhalo/product-image/s3kilvm4zz6e3yleu3xf.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818273/techhalo/product-image/ioxc9n79ltvosvnspxma.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818277/techhalo/product-image/cqzsb1r4aoywl1y7efyu.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818283/techhalo/product-image/krazfnsvqflpey1nteta.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818288/techhalo/product-image/w7n4gdoccbmz1wkpi9qf.webp', 'Chuột Apple Magic Mouse 2021 MK2E3', 2290000, 999, 0, b'1', 5, 14),
	(4, 'MSPTH00004', 'Chuột chơi game không dây DAREU EM911X RGB sở hữu kết nối Wireless 2.4 G đem lại khả năng kết nối cũng như truyền dữ liệu cực nhanh và cho tín hiệu ổn định. Cảm biến của sản phẩm chuột Dareu này cũng rất chính xác.', 30, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732818983/techhalo/product-image/dy5dwguyl6spvplht2d7.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818988/techhalo/product-image/mcrswwchucp52ospcfik.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818994/techhalo/product-image/ad8clxsxwnshreburk7h.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732818999/techhalo/product-image/i5t0y7zykocrlai24a2c.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819004/techhalo/product-image/bekyihpe21ndzk7rgs7f.webp', 'Chuột chơi game không dây Dareu EM911X RGB', 699000, 999, 0, b'1', 5, 15),
	(5, 'MSPTH00005', 'Dareu LM115G là sản phẩm thu hút thuộc dòng chuột không dây tốt được đông đảo người dùng ngay từ những ngày đầu ra mắt. Với thiết kế nhỏ gọn.', 11, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732819337/techhalo/product-image/mdjvckwovnilnn3vjgst.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819342/techhalo/product-image/dbrpg6ny0f5wzyn7h5ph.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819348/techhalo/product-image/avvp3dxyafq7gnq9icgp.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819353/techhalo/product-image/eikzzyrijmrlcobrwwoq.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819359/techhalo/product-image/mcqj4lb2pitzdpsqsail.webp', 'Chuột không dây Dareu LM115G', 169000, 999, 0, b'1', 5, 15),
	(6, 'MSPTH00006', 'Trong các thao tác công việc hằng ngày, đặc biệt là khi làm việc trên laptop hay trên chiếc máy iMac, thì chiếc chuột máy tính không dây Apple Magic Mouse 2 là một điều cần thiết để các thao tác được nhanh nhẹn hơn và linh hoạt hơn.', 0, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732819563/techhalo/product-image/xzbi57ddl7tcaheyk59k.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819570/techhalo/product-image/xgp5f12kh74fwip0gr9y.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819575/techhalo/product-image/q9phwczhdmgwjnxb4rc4.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819581/techhalo/product-image/wjagkhuuscy9iv18obxa.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819588/techhalo/product-image/hec8dkkopc12nhl0ksfn.webp', 'Chuột Apple Magic Mouse 2022 MMMQ3', 2290000, 999, 0, b'1', 5, 14),
	(7, 'MSPTH00007', 'Chuột không dây công thái học Logitech Lift Vertical mang đến cảm giác thoải mái khi cầm nắm làm việc trong thời gian dài từ sáng đến tối. é.', 20, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732819716/techhalo/product-image/u6x2jqkachcyvyqshh0i.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819726/techhalo/product-image/fosdimbmtycqztx93hup.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819731/techhalo/product-image/hncjhjstuwubounbvsvn.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819737/techhalo/product-image/bqpgkw8g2ftzfkiitxvg.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732819741/techhalo/product-image/hplnpbyz80s0npeydtcq.webp', 'Chuột không dây Logitech Lift Vertical', 1490000, 999, 0, b'1', 5, 13),
	(8, 'MSPTH00008', 'Chuột không dây Logitech MX Anywhere 3S với thiết kế kết nối không dây tiện lợi cùng màu đen huyền bí sẽ hài hòa với máy tính bàn hay laptop của bạn. Với tính năng Quiet Clicks của chuột Logitech giúp bạn tập trung vào công việc mà không bị mất tập trung.', 32, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732820509/techhalo/product-image/exflqggfvtvjj0ckg8ad.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820514/techhalo/product-image/tqsljgua1w35lkpbwgef.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820521/techhalo/product-image/ucof9vu2pxhkrtg8qhqh.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820526/techhalo/product-image/igaqiu95cxg1deknlqzs.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820531/techhalo/product-image/ilznnxpcrt7eon0l8iqe.webp', 'Chuột không dây Logitech MX Anywhere 3S', 2190000, 999, 0, b'1', 5, 13),
	(9, 'MSPTH00009', 'Chuột không dây Logitech MX Master 3S mang đến kiểu dáng công thái học, giúp sử dụng thoải mái khi được nâng đỡ cả bàn tay. Phụ kiện chuột Logitech có khả năng sử dụng mượt mà trên nhiều bề mặt.', 23, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732820714/techhalo/product-image/aesafuraq711kfczihb9.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820721/techhalo/product-image/juxu6rzm7rcyrpr3nyeu.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820726/techhalo/product-image/bsjsj5jrexfobwnb148a.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820732/techhalo/product-image/bqbilfze2nezyui2nh15.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820737/techhalo/product-image/he9dap133vfmwcw5imsf.webp', 'Chuột không dây Logitech MX Master 3S', 2990000, 999, 0, b'1', 5, 13),
	(10, 'MSPTH00010', 'Chuột không dây Bluetooth Logitech Pebble M350S là dòng chuột dành đến cho ai thích sự nhỏ gọn để đồng hành lý tưởng cho công việc. Sản phẩm chuột Logitech có thể dễ dàng chuyển đổi thiết bị và giảm ồn tốt hơn đến 90%.', 33, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732820927/techhalo/product-image/vavbvwlivplqf4y6eanl.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820933/techhalo/product-image/nzsrz8pnp7rmb2s8qvgk.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820939/techhalo/product-image/ohzjktxbnacrkrgvoaet.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820945/techhalo/product-image/uchabe3cikzfcukdxtnu.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732820950/techhalo/product-image/whwl1jhhkbclfgplzmro.webp', 'Chuột không dây Bluetooth Logitech Pebble M350S', 699000, 999, 0, b'1', 5, 13),
	(11, 'MSPTH00011', 'Chuột không dây Logitech G Pro X Superlight là mẫu chuột máy tính thế hệ mới được ra mắt. Sản phẩm chuột Logitech này được trang bị thiết kế, kiểu dáng và nhiều công nghệ tiên tiến, hiện đại, nhận được nhiều sự chú ý của người dùng công nghệ. ', 16, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732821062/techhalo/product-image/xjlawpzgpxsdqmkbjhmy.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821068/techhalo/product-image/ijpzsnidjnhygxxdavwq.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821074/techhalo/product-image/k0yr3iiqwynbdpbltd3m.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821079/techhalo/product-image/tzck0ukuv02nkogjhlwn.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821085/techhalo/product-image/ssqvakg7ftibbyxscmpc.webp', 'Chuột không dây Logitech G Pro X 2 Superlight', 3290000, 999, 0, b'1', 5, 13),
	(12, 'MSPTH00012', 'Chuột không dây Rapoo M20 với độ phân giải 1000 DPI và kết nối USB Receiver 2.4GHz giúp đảm bảo độ nhạy và độ ổn định cao trong quá trình sử dụng. Dù chỉ vận hành với mức năng lượng từ 1 pin AA.', 41, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732821269/techhalo/product-image/ylryn7lhyicvi4kimqpg.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821275/techhalo/product-image/xpht2ksbi7cg89cwke8r.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821281/techhalo/product-image/fljpv39zwt9xpbvq6wd1.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821291/techhalo/product-image/s9ssp6fev6w2prjph7c6.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821297/techhalo/product-image/hpsfiy9sykewkzsktrvd.webp', 'Chuột không dây Rapoo M20', 169000, 999, 0, b'1', 5, 16),
	(13, 'MSPTH00013', 'Chuột không dây Logitech G Pro X Superlight là mẫu chuột máy tính thế hệ mới được ra mắt. Sản phẩm chuột Logitech này được trang bị thiết kế, kiểu dáng và nhiều công nghệ tiên tiến, hiện đại, nhận được nhiều sự chú ý của người dùng công nghệ.', 16, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732821395/techhalo/product-image/waidmozwatsuvuvuimwd.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821400/techhalo/product-image/e5u7o9g23qhchvtppr8u.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821406/techhalo/product-image/dbqsrvcrzosmz3exzhn8.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821411/techhalo/product-image/s8iwzzkaarfkdkbg5u1i.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821417/techhalo/product-image/mq5su8jqffntj7ipztjs.webp', 'Chuột không dây Logitech G Pro X Superlight', 3190000, 999, 0, b'1', 5, 13),
	(14, 'MSPTH00014', 'Logitech nhà sản xuất phụ kiện dành cho máy tính nổi tiếng trên thế giới vừa cho ra mắt dòng phụ kiện chuột chơi Game có dây Logitech G502 Hero. Với nhiều tối ưu hóa nâng cấp đặc biệt dành cho chơi game.', 32, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732821625/techhalo/product-image/oizzob4qr4g81s5diusa.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821630/techhalo/product-image/zslsejwic06bbss95yeo.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821635/techhalo/product-image/uubn4rmltw7hqvhmmiox.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821641/techhalo/product-image/ty35q9docvjbgt93k2jr.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821646/techhalo/product-image/zytjxcjqknnajbb1gzi1.webp', 'Chuột Gaming Logitech G502 Hero', 1390000, 998, 1, b'1', 5, 13),
	(15, 'MSPTH00015', 'Chuột không dây Bluetooth Hyperwork Silentium có khả năng đáp ứng tối đa mọi nhu cầu làm việc và giải trí. Với thiết kế công thái học, sản phẩm mang đến trải nghiệm sử dụng thoải mái trong thời gian dài làm việc. Bên cạnh đó.', 21, '2024-11-28', 'https://res.cloudinary.com/techhalo/image/upload/v1732821833/techhalo/product-image/zeelikmrarjakqacsxvn.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821839/techhalo/product-image/d4avbmvcuppoze1ndfsw.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821844/techhalo/product-image/nugzxuilbmuffyeqekck.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821854/techhalo/product-image/hua97zfqgjob1tmpjhze.webp', 'https://res.cloudinary.com/techhalo/image/upload/v1732821859/techhalo/product-image/zapenw1rujrzyfucusfh.webp', 'Chuột không dây Bluetooth Hyperwork Silentium', 590000, 999, 0, b'1', 5, 17),
