import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd  } from '@angular/router';
import { Renderer2 } from '@angular/core';  // Import Renderer2 để thao tác với DOM
import { Meta } from '@angular/platform-browser';  // Import Meta để thao tác với các thẻ meta
import { filter } from 'rxjs/operators';  // Import RxJS filter để lắng nghe sự kiện Router

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  showHeaderFooter: boolean = true;
  showZaloChat: boolean = true; // Điều khiển hiển thị widget Zalo
  title = 'techhalo';

  constructor(
    private router: Router,
    private renderer: Renderer2, // Dùng Renderer2 để thao tác với DOM
    private meta: Meta // Dùng Meta để thao tác với các thẻ meta
  ) {
    // Lắng nghe sự kiện thay đổi route
    this.router.events.pipe(
      filter(event => event instanceof NavigationEnd)  // Lọc sự kiện NavigationEnd
    ).subscribe(() => {
      // Kiểm tra nếu route chứa `/admin`, ẩn header/footer và widget Zalo
      this.showHeaderFooter = !this.router.url.startsWith('/admin');
      this.showZaloChat = !this.router.url.startsWith('/admin');
    });
  }

  ngOnInit() {
    // Thêm meta tag viewport vào trang khi ứng dụng khởi chạy
    this.addMetaViewport();

    // Chỉ tải SDK Zalo khi ứng dụng được tải
    const script = document.createElement('script');
    script.src = 'https://sp.zalo.me/plugins/sdk.js';
    script.async = true;
    document.body.appendChild(script);
  }

  // Hàm để thêm meta tag viewport
  private addMetaViewport() {
    // Kiểm tra xem meta tag đã tồn tại chưa
    let metaTag = document.querySelector('meta[name="viewport"]');

    if (!metaTag) {
      // Nếu chưa tồn tại, tạo mới
      metaTag = this.renderer.createElement('meta');
      this.renderer.setAttribute(metaTag, 'name', 'viewport');
      this.renderer.setAttribute(metaTag, 'content', 'width=device-width, initial-scale=0.75');
      this.renderer.appendChild(document.head, metaTag);
    } else {
      // Nếu đã tồn tại, cập nhật nội dung
      this.renderer.setAttribute(metaTag, 'content', 'width=device-width, initial-scale=0.75');
    }
  }
}
